import React from "react";
import "./Button.css";

class Button extends React.Component {
  isOperator = val => {
    return !isNaN(val) || val === "." || val === "=" || val === "CE";
  };

  render() {
    return (
      <div
        className={`button  ${
          this.isOperator(this.props.children) ? " " : "operator"
        }`}
        onClick={this.addToInput(this.props.children)}
      >
        {this.props.children}
      </div>
    );
  }
}
export default Button;
